    &__results {
        height: 100%;

        &-item {
            margin: 5px 0;
            padding: 10px;
            cursor: pointer;

            &.active {
                background-color: #fff;
            }
        }
    }

    &__results-loader {
        position: absolute;
        top: 0;
        bottom: 0;
        left: 0;
        right: 0;
        display: flex;
        align-items: center;
        justify-content: center;
        background-color: rgba(255, 255, 255, .6);
        opacity: 0;
        .lds-roller {
            display: inline-block;
            position: relative;
            width: 80px;
            height: 80px;

            div {
                animation: lds-roller 1.2s cubic-bezier(0.5, 0, 0.5, 1) infinite;
                transform-origin: 40px 40px;

                &::after{
                    content: " ";
                    display: block;
                    position: absolute;
                    width: 7px;
                    height: 7px;
                    border-radius: 50%;
                    background: #fff;
                    margin: -4px 0 0 -4px;
                }

                &:nth-child(1) {
                    animation-delay: -0.036s;

                    &::after {
                        top: 63px;
                        left: 63px;
                    }
                }

                &:nth-child(2) {
                    animation-delay: -0.072s;

                    &::after {
                        top: 68px;
                        left: 56px;
                    }
                }

                &:nth-child(3) {
                    animation-delay: -0.108s;

                    &::after {
                        top: 71px;
                        left: 48px;
                    }
                }

                &:nth-child(4) {
                    animation-delay: -0.144s;

                    &::after {
                        top: 72px;
                        left: 40px;
                    }
                }

                &:nth-child(5) {
                    animation-delay: -0.18s;

                    &::after {
                        top: 71px;
                        left: 32px;
                    }
                }

                &:nth-child(6) {
                    animation-delay: -0.216s;

                    &::after {
                        top: 68px;
                        left: 24px;
                    }
                }

                &:nth-child(7) {
                    animation-delay: -0.252s;

                    &::after {
                        top: 63px;
                        left: 17px;
                    }
                }

